package loops;

public class while_dowhile_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
//	for loop	
		int i=1 ;
		for(;i<=10;) {
			System.out.print(" "+ i);
			i++;
		}
		
		System.out.println();
		
//		while loop
		int j=1;
		while(j<=9) {
			System.out.print(" "+j);
			j++;
		}
		
		System.out.println();
		
//		do while 
		int k=1;
		do {
			System.out.print(" "+k);
			k++;
		}while(k<=8);
		
		
	}

}
